import { Injectable } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { ValidationMessages } from '../interfaces/Validation-messages.interface';

@Injectable({
  providedIn: 'root'
})
export class NgxValidationHandlingService {
  private form!: FormGroup;
  private ValidationMessages!: ValidationMessages;

  setValidationMessages(form: FormGroup, ValidationMessages?: ValidationMessages): void {
    this.form = form;
    this.ValidationMessages = ValidationMessages || {
      required: 'This field is required',
      minlength: 'The value is too short',
      maxlength: 'The value is too long',
      email: 'Invalid email address',
    };
  }
  getValidationMessages(): ValidationMessages {
    return this.ValidationMessages;
  }
  getForm(): FormGroup {
    return this.form;
  }
}
import { Injectable } from '@angular/core';
import { ValidationErrors } from '@angular/forms';
import { NgxValidationHandlingService } from './ngx-validation-handling.service';
@Injectable({
    providedIn: 'root'
})
export class NgxValidationHandlingMaintainService {
    constructor(private ngxValidationHandlingService: NgxValidationHandlingService) { }
    getErrorMessage(controlName: string): string {
        const form = this.ngxValidationHandlingService.getForm();
        const validationMessages = this.ngxValidationHandlingService.getValidationMessages();
        const control = form.get(controlName);
        if (control && control.errors) {
            const errors: ValidationErrors = control.errors;
            for (const error in errors) {
                if (errors.hasOwnProperty(error)) {
                    return validationMessages[error] || 'Invalid field';
                }
            }
        }
        return '';
    }
}
import { Component, Input } from '@angular/core';
import { NgxValidationHandlingMaintainService } from '../../services/ngx-validation-handling-maintain.service';
@Component({
  selector: 'ngx-validation-handling',
  template: `
    <ng-container *ngIf="controlName">
      <label [ngStyle]="style"  *ngIf="errorMessage !== ''" class="error-message">{{ errorMessage }}</label>
    </ng-container>
  `,
  styles: [
    `
    .error-message {
      color: red;
      font-size: 12px;
    }
  `
  ],
})
export class NgxValidationHandlingComponent {
  @Input() controlName!: string;
  @Input() style!: any;

  constructor(private ngValidationMessagesMaintainService: NgxValidationHandlingMaintainService) { }

  get errorMessage(): string {
    return this.ngValidationMessagesMaintainService.getErrorMessage(this.controlName);
  }
}
import { NgModule } from '@angular/core';
import { NgxValidationHandlingComponent } from './components/ngx-validation-handling/ngx-validation-handling.component';
import { CommonModule } from '@angular/common';


@NgModule({
  declarations: [
    NgxValidationHandlingComponent
  ],
  imports: [
    CommonModule
  ],
  exports: [
    NgxValidationHandlingComponent
  ]
})
export class NgxValidationHandlingModule { }
/*
 * Public API Surface of ngx-validation-handling
 */

export * from './lib/services/ngx-validation-handling.service';
export * from './lib/components/ngx-validation-handling/ngx-validation-handling.component';
export * from './lib/ngx-validation-handling.module';
/**
 * Generated bundle index. Do not edit.
 */

export * from './public-api';
